import React from "react";
import logo from "../assets/logo.png"; // Ensure the path is correct.

const HeroSection = () => {
  return (
    <div className="relative w-full h-screen">
      {/* Header Section */}
      <header className="absolute top-0 left-0 w-full flex justify-between items-center p-4 bg-black bg-opacity-50">
        {/* Logo */}
        <div className="flex items-center">
          <img src={logo} alt="RC Studios Logo" className="h-12" />
          <h1 className="text-white text-2xl font-bold ml-2">RC Studios</h1>
        </div>

        {/* Navigation Buttons */}
        <div className="flex items-center">
          <a
            href="https://calendar.google.com/"
            target="_blank"
            rel="noopener noreferrer"
            className="text-white bg-blue-600 px-4 py-2 rounded-md hover:bg-blue-500 transition"
          >
            Book Consultation
          </a>
        </div>
      </header>

      {/* Video Background */}
      <video
        className="absolute top-0 left-0 w-full h-full object-cover"
        autoPlay
        loop
        muted
      >
        <source src="/sample-video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Overlay with Text */}
      <div className="absolute top-0 left-0 w-full h-full bg-black bg-opacity-50 flex flex-col justify-center items-center text-center text-white">
        <h1 className="text-4xl md:text-6xl font-bold">RC Studios</h1>
        <p className="text-xl md:text-2xl mt-4">Capturing Your Moments, Perfectly.</p>
      </div>
    </div>
  );
};

export default HeroSection;
