import React from "react";
import photo1 from "../assets/photo1.jpg";
import photo2 from "../assets/photo2.jpg";
import photo3 from "../assets/photo3.jpg";

const Portfolio = () => {
  const images = [photo1, photo2, photo3];
  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 p-8">
      {images.map((image, idx) => (
        <div key={idx} className="relative group">
          <img
            src={image}
            alt={`Portfolio ${idx + 1}`}
            className="w-full h-auto rounded-lg shadow-lg group-hover:scale-105 transition-transform duration-300"
          />
          {/* Optional: Add overlay or text on hover */}
          <div className="absolute top-0 left-0 w-full h-full bg-black bg-opacity-50 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex justify-center items-center text-white">
            <span className="text-xl">View Details</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Portfolio;
